<script setup>
import { ref } from 'vue';

const code = ref('12345');
const count = ref(5);
const counting = ref(false);
let timer;

function startCountdown() {
    if (counting.value) return;

    counting.value = true;
    count.value = 5;

    timer = setInterval(() => {
        count.value--;
        if (count.value <= 0) {
            clearInterval(timer);
            counting.value = false;
        }
    }, 1000);
}
</script>

